mapper.select:
SELECT *
FROM T4_USER
WHERE U_NAME = #{uName} AND U_PHONE = #{uPhone}

SELECT *
FROM T4_USER
WHERE U_NAME = #{uName} AND U_PHONE = #{uPhone} AND U_ID =#{uId}

SELECT COUNT(*) AS CNT
FROM T4_USER
WHERE U_ID = #{uId}

SELECT * FROM T4_USER
WHERE U_ID = #{uId}
LIMIT #{startNum}, #{lastNum}
